import { prismaObjectType } from "nexus-prisma";
import { createUser, updateUser} from "./user/user-resolvers";
import { createBill, updateBill} from "./bill/bill-resolvers";
import {createTransaction, updateTransaction} from "./transaction/transaction-resolvers";

