// @ts-ignore
export const Mutation = prismaObjectType({
    name: "Mutation",
    definition(t) {
        // t.prismaFields(["*"]); expose all the mutations generated by prisma
        createUser(t);
        updateUser(t);
        createBill(t);
        updateBill(t);
        createTransaction(t);
        updateTransaction(t);
    },
});
